using NianiakoudisSite.Models;

namespace NianiakoudisSite.Services;

public sealed class PageContentService : IPageContentService
{
    public PageContent GetHome()
    {
        return new PageContent(
            "\u03A3\u03C4\u03C1\u03B1\u03C4\u03B7\u03B3\u03B9\u03BA\u03AE, \u03B5\u03BC\u03C0\u03B5\u03B9\u03C1\u03AF\u03B1 \u03BA\u03B1\u03B9 \u03C3\u03CD\u03B3\u03C7\u03C1\u03BF\u03BD\u03BF web.",
            new[]
            {
                "\u0395\u03BE\u03B5\u03B9\u03B4\u03B9\u03BA\u03B5\u03C5\u03BC\u03AD\u03BD\u03B5\u03C2 \u03BB\u03CD\u03C3\u03B5\u03B9\u03C2 \u03B3\u03B9\u03B1 \u03B5\u03C0\u03B9\u03C7\u03B5\u03B9\u03C1\u03AE\u03C3\u03B5\u03B9\u03C2 \u03BA\u03B1\u03B9 \u03BF\u03C1\u03B3\u03B1\u03BD\u03B9\u03C3\u03BC\u03BF\u03CD\u03C2.",
                "\u03A0\u03BF\u03B9\u03CC\u03C4\u03B7\u03C4\u03B1, \u03C3\u03C5\u03BD\u03AD\u03C0\u03B5\u03B9\u03B1 \u03BA\u03B1\u03B9 \u03B1\u03BD\u03B8\u03C1\u03CE\u03C0\u03B9\u03BD\u03B7 \u03C0\u03C1\u03BF\u03C3\u03AD\u03B3\u03B3\u03B9\u03C3\u03B7.",
                "\u03A3\u03C7\u03B5\u03B4\u03B9\u03B1\u03C3\u03BC\u03CC\u03C2 \u03C0\u03BF\u03C5 \u03BA\u03AC\u03BD\u03B5\u03B9 \u03C4\u03B7 \u03B4\u03B9\u03B1\u03C6\u03BF\u03C1\u03AC \u03C3\u03C4\u03B7\u03BD \u03B5\u03B9\u03BA\u03CC\u03BD\u03B1 \u03C3\u03B1\u03C2."
            },
            "\u03A7\u03C4\u03AF\u03B6\u03BF\u03C5\u03BC\u03B5 \u03BC\u03B9\u03B1 \u03BE\u03B5\u03BA\u03AC\u03B8\u03B1\u03C1\u03B7 \u03C8\u03B7\u03C6\u03B9\u03B1\u03BA\u03AE \u03C0\u03B1\u03C1\u03BF\u03C5\u03C3\u03AF\u03B1 \u03C0\u03BF\u03C5 \u03C3\u03C4\u03B7\u03C1\u03AF\u03B6\u03B5\u03B9 \u03C4\u03BF\u03C5\u03C2 \u03C3\u03C4\u03CC\u03C7\u03BF\u03C5\u03C2 \u03C3\u03B1\u03C2 \u03BA\u03B1\u03B9 \u03B5\u03BD\u03B9\u03C3\u03C7\u03CD\u03B5\u03B9 \u03C4\u03B7\u03BD \u03B1\u03BE\u03B9\u03BF\u03C0\u03B9\u03C3\u03C4\u03AF\u03B1 \u03C4\u03BF\u03C5 brand \u03C3\u03B1\u03C2."
        );
    }

    public PageContent GetAbout()
    {
        return new PageContent(
            "\u0395\u03BC\u03C0\u03B5\u03B9\u03C1\u03AF\u03B1, \u03BC\u03B5\u03B8\u03BF\u03B4\u03B9\u03BA\u03CC\u03C4\u03B7\u03C4\u03B1 \u03BA\u03B1\u03B9 \u03C3\u03C5\u03BD\u03B5\u03C1\u03B3\u03B1\u03C3\u03AF\u03B1.",
            new[]
            {
                "\u0391\u03BD\u03AC\u03BB\u03C5\u03C3\u03B7 \u03B1\u03BD\u03B1\u03B3\u03BA\u03CE\u03BD \u03BA\u03B1\u03B9 \u03C3\u03C4\u03BF\u03C7\u03B5\u03C5\u03BC\u03AD\u03BD\u03B7 \u03C3\u03C4\u03C1\u03B1\u03C4\u03B7\u03B3\u03B9\u03BA\u03AE.",
                "\u03A0\u03C1\u03BF\u03C3\u03B5\u03B3\u03BC\u03AD\u03BD\u03B7 \u03C5\u03BB\u03BF\u03C0\u03BF\u03AF\u03B7\u03C3\u03B7 \u03BC\u03B5 \u03AD\u03BC\u03C6\u03B1\u03C3\u03B7 \u03C3\u03C4\u03B7 \u03BB\u03B5\u03C0\u03C4\u03BF\u03BC\u03AD\u03C1\u03B5\u03B9\u03B1.",
                "\u03A3\u03C5\u03BD\u03B5\u03C7\u03AE\u03C2 \u03C5\u03C0\u03BF\u03C3\u03C4\u03AE\u03C1\u03B9\u03BE\u03B7 \u03BA\u03B1\u03B9 \u03B5\u03BE\u03AD\u03BB\u03B9\u03BE\u03B7."
            },
            "\u0394\u03BF\u03C5\u03BB\u03B5\u03CD\u03BF\u03C5\u03BC\u03B5 \u03B4\u03AF\u03C0\u03BB\u03B1 \u03C3\u03B1\u03C2 \u03B3\u03B9\u03B1 \u03BD\u03B1 \u03BC\u03B5\u03C4\u03B1\u03C4\u03C1\u03AD\u03C8\u03BF\u03C5\u03BC\u03B5 \u03C4\u03B9\u03C2 \u03B9\u03B4\u03AD\u03B5\u03C2 \u03C3\u03B5 \u03B1\u03C0\u03BF\u03C4\u03B5\u03BB\u03B5\u03C3\u03BC\u03B1\u03C4\u03B9\u03BA\u03AD\u03C2 \u03BB\u03CD\u03C3\u03B5\u03B9\u03C2, \u03BC\u03B5 \u03C3\u03B1\u03C6\u03AE\u03BD\u03B5\u03B9\u03B1, \u03B1\u03BE\u03B9\u03BF\u03C0\u03B9\u03C3\u03C4\u03AF\u03B1 \u03BA\u03B1\u03B9 \u03B4\u03B9\u03B1\u03C6\u03AC\u03BD\u03B5\u03B9\u03B1."
        );
    }
}
